Insert into SALUDMP.VDIR_PROGRAMA (COD_PROGRAMA,DES_PROGRAMA,COD_PRODUCTO,COD_ESTADO,COD_IMPUESTO,URL_CONTRATO,DESCRIPCION) values ('3','Oro plus','1','1',null,null,'Es un programa de aseguramiento voluntario que cuenta con beneficios como, consulta por fuera de la red, atención médica a domicilio con Coomeva Emergencia Médica 2 veces al año, enfermera acompañante, renta diaria por hospitalización, ambulancia aérea, trastornos congénitos y genéticos, prótesis y ortesis, urgencias en el exterior  50 mil USD, menores periodos de carencia y entre otros.');
Insert into SALUDMP.VDIR_PROGRAMA (COD_PROGRAMA,DES_PROGRAMA,COD_PRODUCTO,COD_ESTADO,COD_IMPUESTO,URL_CONTRATO,DESCRIPCION) values ('1','Coomeva Emergencia Médica','3','1',null,null,'Es un servicio de Atención Médica a Domicilio, Urgencias y Emergencias, orientación médica telefónica y traslado de pacientes. En la comodidad de ser atendido en el lugar donde se encuentre, las 24 horas al día, los 365 días del año, excelente atención con personal (Médico y Paramédico) altamente calificado. Adicionales, orientación medica telefónica, tele salud nutrición y psicología, urgencias odontológicas domiciliarias.');
Insert into SALUDMP.VDIR_PROGRAMA (COD_PROGRAMA,DES_PROGRAMA,COD_PRODUCTO,COD_ESTADO,COD_IMPUESTO,URL_CONTRATO,DESCRIPCION) values ('4','Plata joven','1','1',null,null,'Programa dirigido a población joven menores de 35 años, con amplias coberturas ambulatorias (consulta, terapias, ayudas diagnosticas), maternidad, medicina alternativa, procedimientos quirúrgicos y hospitalario con topes de cobertura y bolsa de hospitalización reinstalable por diagnóstico. Directorio abierto más de 10.000 prestadores entre especialistas e instituciones de salud a nivel nacional.');
Insert into SALUDMP.VDIR_PROGRAMA (COD_PROGRAMA,DES_PROGRAMA,COD_PRODUCTO,COD_ESTADO,COD_IMPUESTO,URL_CONTRATO,DESCRIPCION) values ('5','Tradicional especial','1','1',null,null,'Es un programa de aseguramiento voluntario que cubre hasta 7 especialidades básicas, endoscopia digestiva alta, 100% en urgencias y hospitalización en red especial, maternidad,  y suministros; así como otras coberturas especiales.');
Insert into SALUDMP.VDIR_PROGRAMA (COD_PROGRAMA,DES_PROGRAMA,COD_PRODUCTO,COD_ESTADO,COD_IMPUESTO,URL_CONTRATO,DESCRIPCION) values ('6','Asociado','1','1',null,null,'Dirigido exclusivamente para los asociados a la Cooperativa Coomeva con edad de ingreso hasta los 65 años, con amplias coberturas ambulatorias, procedimientos quirúrgicos y hospitalario con topes de cobertura amplios y bolsa de hospitalización reinstalable por diagnóstico, directorio abierto con más de 10.000 prestadores entre especialistas e instituciones de salud a nivel nacional.');
