  FUNCTION send_email(REMITE IN VARCHAR2,DESTINO IN VARCHAR2, ASUNTO IN VARCHAR2, MENSAJE IN VARCHAR2,PUERTO IN NUMBER,SERVIDOR IN VARCHAR2,USUARIO IN VARCHAR2,CLAVE IN VARCHAR2)
RETURN VARCHAR2
AS                                                         
