      JSON_DATOS:= JSON_DATOS ||'"NOMBRE": "'||FILA.DES_ROL||'"';
      JSON_DATOS:= JSON_DATOS ||'},';

   END LOOP;
    JSON_DATOS:=SUBSTR(JSON_DATOS, 1,LENGTH(JSON_DATOS)-1);
    JSON_DATOS:= JSON_DATOS || ']'; 

    RETURN json_datos; 

