 FUNCTION fn_splitData
 (
    P_STRING_DATA IN VARCHAR2,
    P_SEPARATOR IN VARCHAR2
 )RETURN datasplit_table PIPELINED
 IS
     ltc_datos type_cursor;
     rec datasplit_record;
