    FOR registro IN  (SELECT level AS IDX, regexp_substr(P_STRING_DATA,'[^'|| P_SEPARATOR ||']+', 1, level) DATO from dual
                    connect by regexp_substr(P_STRING_DATA, '[^'|| P_SEPARATOR ||']+', 1, level) is not null)
    LOOP
        SELECT 
            registro.idx, 
