		OPEN cu_beneficiarios; 
	    LOOP 
	    FETCH cu_beneficiarios INTO ltc_beneficiarios; 
		    EXIT WHEN cu_beneficiarios%NOTFOUND; 

		    lvc_url := lvc_url||chr(38)||'txtNombreBeneficiario'||lnu_i||'='||ltc_beneficiarios.nombreBeneficiario;
			lvc_url := lvc_url||chr(38)||'txtNumIdBeneficiario' ||lnu_i||'='||ltc_beneficiarios.numeroIdentificacion;

