						-- ---------------------------------------------------------------------
						-- Se ingresa el usuario
						-- --------------------------------------------------------------------- 
						INSERT INTO VDIR_USUARIO
						(
							COD_USUARIO,
							LOGIN,
							CLAVE,
							COD_PERSONA,
							COD_ESTADO
						) 
						VALUES 
						(
							lnu_codUsuario,
							ivc_login,
							ivc_clave,
							lnu_codPersona,
							inu_codEstado
						);

					EXCEPTION WHEN OTHERS THEN

						lnu_error := 1;
						RAISE_APPLICATION_ERROR(-20001,'Ocurrió un error al guardar el usuario: '||SQLERRM); 

					END; 

					-- ---------------------------------------------------------------------
					-- Si no ocurrió ningun error
					-- --------------------------------------------------------------------- 
					IF lnu_error = 0 THEN

					    -- ---------------------------------------------------------------------
						-- Se valida si el perfil existe
						-- ---------------------------------------------------------------------
						lnu_codRolUsuario := VDIR_PACK_CONSULTA_USUARIOS.fnGetExistePerfil(lnu_codUsuario,inu_codPerfil);  

						-- ---------------------------------------------------------------------
						-- Si el perfil no existe
