							-- --------------------------------------------------------------------- 
							SELECT VDIR_SEQ_ROL_USUARIO.NEXTVAL INTO lnu_codRolUsuario FROM DUAL;   

							BEGIN

								-- ---------------------------------------------------------------------
								-- Se ingresa el perfil
								-- --------------------------------------------------------------------- 
								INSERT INTO VDIR_ROL_USUARIO
								(
								    COD_ROL_USUARIO,
									COD_USUARIO,
									COD_ROL
								) 
								VALUES 
								(
									lnu_codRolUsuario,
									lnu_codUsuario,
									inu_codPerfil
								);

							EXCEPTION WHEN OTHERS THEN

								lnu_error := 1;
								RAISE_APPLICATION_ERROR(-20001,'Ocurrió un error al guardar el perfil: '||SQLERRM); 

							END; 						

						END IF;

					END IF;			

				END IF;

			END IF;

		END IF;

	END prGuardarUsuario;

